import React, { Component, useState } from "react";
import axios from "axios";
import content from "../inputs/content/content.png";
import style from "../inputs/style/style.png";
import result from "../outputs/result.png";

class AddStyle extends Component{
  constructor() {
    super();
    this.state = {
      content: null,
      style: null,
    };
  };

  onContentChange = (e)=>{
    this.setState({
      content: e.target.files[0],
      style: e.target.files[1],
    });
  };

  onStyleChange = (e)=>{
    this.setState({
      content: e.target.files[0],
      style: e.target.files[1],
    });
  };

  onFormSubmit = ()=>{
    const formData = new FormData();
    formData.append('content', this.state.content);
    formData.append('style', this.state.style);
    const config = {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    };
    axios
      .post("http://localhost:5000/queryAddStyle", formData, config)
      .then((response) => {
        alert('Successful');
      })
      .catch((err) => {
        console.log('err', err);
      });
  };

  render() {
    return (
      <>
        <div className="container my-3">
          <form onSubmit={this.onFormSubmit}>
            <div className="mb-3">
              <h1>Upload Images Here</h1>
            </div>
            <div className="mb-3">
                <label htmlFor="content" className="form-label">Content Image</label>
                <input type="file" className="form-control" id="content" onChange={this.onContentChange} />
            </div>
            <div className="mb-3">
                <label htmlFor="style" className="form-label">Style Image</label>
                <input type="file" className="form-control" id="style" onChange={this.onStyleChange} />
            </div>
            <button type="submit" className="btn btn-primary" >Submit</button>
          </form>
        </div>
        <div className="container my-3">
            <h1>See Results Here</h1>
            <div className="row">
                <div className="col-sm-4">
                    <div className="card" style={{width: '18rem'}}>
                        <img src={content} className="card-img-top" alt="..." />
                        <div className="card-body">
                            <h5 className="card-title">Content Image</h5>
                            <p className="card-text">This is content.</p>
                        </div>
                    </div>
                </div>
                <div className="col-sm-4">
                    <div className="card" style={{width: '18rem'}}>
                        <img src={style} className="card-img-top" alt="..." />
                        <div className="card-body">
                            <h5 className="card-title">Style Image</h5>
                            <p className="card-text">This is style.</p>
                        </div>
                    </div>
                </div>
                <div className="col-sm-4">
                    <div className="card" style={{width: '18rem'}}>
                        <img src={result} className="card-img-top" alt="..." />
                        <div className="card-body">
                            <h5 className="card-title">Result Image</h5>
                            <p className="card-text">This is result.</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
      </>
    );
  };
};

export default AddStyle;
